mapper.select:
SELECT STU_NO, NAME, KOR, ENG, MAT, AVG, GRADE
	FROM STUDENT
WHERE STU_NO = #{stuNo}

SELECT STU_NO, NAME, KOR, ENG, MAT, AVG, GRADE
  FROM STUDENT
 WHERE AVG >= #{begin} AND AVG <= #{end}

SELECT COUNT(*)
  FROM STUDENT
 WHERE AVG >= #{begin} AND AVG <= #{end}

SELECT NVL(AVG(AVG), 0)
  FROM STUDENT
 WHERE AVG >= #{begin} AND AVG <= #{end}
