mapper.select:
		SELECT A.*
		  FROM (SELECT ROW_NUMBER() OVER(ORDER BY E.HIRE_DATE DESC) AS RN, E.*, D.DEPARTMENT_NAME
		          FROM DEPARTMENTS D RIGHT JOIN EMPLOYEES E
		            ON D.DEPARTMENT_ID = E.DEPARTMENT_ID


				 		${column} = TRIM(#{query})
				 		LOWER(${column}) LIKE '%' || LOWER(#{query}) || '%'
				 		${column} BETWEEN TO_DATE(#{begin}, 'YYYY-MM-DD') AND TO_DATE(#{end}, 'YYYY-MM-DD')
		 				${column} BETWEEN #{begin} AND #{end}

				) A
         WHERE A.RN BETWEEN #{beginRecord} AND #{endRecord}

SELECT DISTINCT ${column}
  FROM EMPLOYEES
 WHERE ${column} LIKE #{query} || '%'
